
export function cut(text, params) {
  let { cutLen, softcut, fill } = Object.assign({}, { cutLen: 30, softcut: true, fill: ''}, params);

  // nothing to do
  if (cutLen > text.length) {
    return text;
  }

  // text >= 30
  let cutText = text.substr(0, cutLen);
  let cutText2 = cutText;
  // cut on last blank
  if (softcut) {
    let n = cutText.lastIndexOf(' ');
    if (n > 0) {
      cutText2 = cutText.substr(0, n);
    }
  }
  // concat
  let cutText3 = cutText2;
  if (fill.length) {
    cutText3 = cutText2 + fill;
  }
  return cutText3;

}
