/* eslint-disable no-console */
import NetMetrixTracker from 'helpers/net-metrix-tracker';

export default {

  track(event, id, index) {
    let path = window.location.pathname;

    // remove slashes at start and end
    path = path.replace(/^[\/]*/, '').replace(/[\/]*$/, '');

    let options = {
      domain: 'aznetz',
      path: {
        product: 'live',
        sitename: this.props.site,
        view: this.constructor.displayName.toLowerCase(),
        path: path,
        event: event.toLowerCase(),
        id: id,
        index: index
      },
      screenWidth: screen.width,
      screenHeight: screen.height
    };
    let tracker = new NetMetrixTracker(options);

    // track Net Metrix
    let tag = tracker.track();

    console.info('Net Metrix Tag (currently inactive: ', tag);
  }

};
