/* eslint-disable no-console */
import NetMetrixTracker from 'helpers/net-metrix-tracker';

export default {

  track(event, id, index) {
    let options = {
      view: this.constructor.displayName,
      event: event,
      path: window.location.pathname,
      id: id,
      index: index
    };
    let tracker = new NetMetrixTracker(options);

    // track Net Metrix
    let tag = tracker.track();

    console.info('-- tracked Net Metrix page impression --', tag, options);
  }

};
