import React from 'react';

import ArticleHeader from 'components/article-header';
import ArticleContent from 'components/article-content';
import Comments from 'components/comments';
import RecommendationsBottomPrev from 'components/recommendations-bottom-prev';

export default React.createClass({

  displayName: 'ArticlePage',

  render() {
    return (
      <article itemScope itemType='http://schema.org/Article'>
        <ArticleHeader {...this.props} />
        <ArticleContent {...this.props} />
        <Comments />
        <RecommendationsBottomPrev teasers={this.props.teasers} />
      </article>
    );
  }

});
