import React from 'react';

import Container from 'components/container';
import VideoLibraryHeader from 'components/video-library-header';
import VideoLibraryContent from 'components/video-library-content';
import VideoLibraryPreview from 'components/video-library-preview';

export default React.createClass({

  displayName: 'VideoLibrary',

  propTypes: {
    videos: React.PropTypes.arrayOf(React.PropTypes.object)
  },

  render() {
    return (
      <Container>
        <VideoLibraryHeader/>
        <VideoLibraryContent/>
        <VideoLibraryPreview videos={this.props.videos.objects}/>
      </Container>
    );
  }

});
