import React from 'react';
import BEM from 'react-bem-helper';

import SectionHeader from 'components/section-header';
import WeatherDetail from 'components/weather-detail';

const classes = new BEM('Weather');

export default React.createClass({

  displayName: 'Weather',

  propTypes: {
    weatherIcon: React.PropTypes.string.isRequired,
    minTemperature: React.PropTypes.number.isRequired,
    maxTemperature: React.PropTypes.number.isRequired,
    weatherText: React.PropTypes.string.isRequired
  },

  render() {

    return (
      <div {...classes()}>
        <SectionHeader title='Wetter' titleModifier='large' />
        <WeatherDetail
          {...this.props}
        />
      </div>
    );
  }
});
