'use strict';

const React = require('react');
const bem = require('react-bem-helper');

const Color = require('styleguide/color');

const classes = new bem({
  name: 'color-palette',
  prefix: 'styleguide-'
});

const TypographyItem = React.createClass({

  render() {
    return (
      <div {...classes()}>
        <h2 {...classes('title')}>{this.props.title}</h2>
        <ul {...classes('list')}>
          {this.props.colors.map((color, index) =>
            <Color key={index} {...color} />
          )}
        </ul>
      </div>
    );
  }

});

module.exports = TypographyItem;
