'use strict';

const React = require('react');
const bem = require('react-bem-helper');

const classes = new bem({
  name: 'color-palette',
  prefix: 'styleguide-'
});

const TypographyItem = React.createClass({

  render: function() {
    return (
      <li {...classes('item')}>
        <div {...classes('inner')}>
          <div {...classes('flip')}>
            <div {...classes('recto')} style={{backgroundColor: this.props.value}}>Preview for {this.props.name} color ({this.props.value})</div>
            <div {...classes('verso')}>{this.props.description}</div>
          </div>
          <div {...classes('info')}>
            <p {...classes('name')}>{this.props.name}</p>
            <p {...classes('value')}>{this.props.value}</p>
          </div>
        </div>
      </li>
    );
  }

});

module.exports = TypographyItem;
