'use strict';

const React = require('react');
const ReadMe = require('styleguide/readme');
const Viewer = require('styleguide/viewer');
const Variations = require('styleguide/variations');
const DataStructure = require('styleguide/data-structure');

var Component = React.createClass({

  propTypes: {
    readme: React.PropTypes.string,
    component: React.PropTypes.object,
  },

  constructUrl(component) {
    return '/styleguide/component/' + component.slug + '/full';
  },

  render() {
    var component = this.props.component, dataStructure;

    if (component.variation && component.variation.data) {
      dataStructure = <DataStructure data={component.variation.data} />;
    }

    return (
      <div>
        <ReadMe {...component} />
        <Viewer src={this.constructUrl(component)}/>
        <Variations variations={component.variations} />
        {dataStructure}
      </div>
    );
  }

});

module.exports = Component;
