'use strict';

const React = require('react');
const slugify = require('slugify');

const ReadMe = require('styleguide/readme');
const Viewer = require('styleguide/viewer');
const Variations = require('styleguide/variations');

const Component = React.createClass({

  propTypes: {
    readme: React.PropTypes.string,
    children: React.PropTypes.element.isRequired,
    component: React.PropTypes.object.isRequired,
    variation: React.PropTypes.object,
  },

  mapVariationItems(variations) {
    var props = this.props;
    return variations.map(function(variation) {
      var variationItem = {};
      var slug = slugify(variation.title).toLowerCase();
      variationItem.title = variation.title;
      variationItem.link = '/styleguide/component/' + props.component.slug + '/' + slug;
      variationItem.slug = slug;
      variationItem.active = slug === props.variation.slug;
      return variationItem;
    });
  },

  render() {
    var component = this.props.component;
    var variation = this.props.variation;
    var variations = component.variations && component.variations.length > 1 ? <Variations variations={this.mapVariationItems(component.variations)} /> : false;

    var render = component.variations
      ? <Viewer component={this.props.component} json={variation ? variation.data : false} variation={variation}>{this.props.children}</Viewer>
      : this.props.children;

    return (
      <div>
        <ReadMe {...component} />
        {render}
        {variations}
      </div>
    );
  }

});

module.exports = Component;
