'use strict';

const React = require('react');
const Highlight = require('react-highlight');

const DataSructure = React.createClass({

  propTypes: {
    json: React.PropTypes.object,
  },

  render() {

    var content;

    if (this.props.json) {
      content = <Highlight className='json'>{JSON.stringify(this.props.json, null, 2)}</Highlight>;
    } else {
      content = (
        <p>You should add sample data to <code>.variations.json</code> in the root of this component!</p>
      );
    }

    return (
      <div>
        <h2>Data structure</h2>
        {content}
      </div>
    );

  }

});

module.exports = DataSructure;
