import React from 'react';
import Highlight from 'react-highlight';

import Loading from 'styleguide/loading';

export default React.createClass({

  displayName: 'DataStructure',

  propTypes: {
    json: React.PropTypes.object
  },

  render() {
    var content;

    if (this.props.json) {
      content = <Highlight className='json'>{JSON.stringify(this.props.json, null, 2)}</Highlight>;
    } else {
      content = (
        <p>You should add sample data to <code>.variations.json</code> in the root of this component!</p>
      );
    }

    return (
      <section>
        <h2>Data structure</h2>
        <Loading>
          {content}
        </Loading>
      </section>
    );
  }

});
