'use strict';

const React = require('react');
const bem = require('react-bem-helper');

var LayoutFull = React.createClass({

  propTypes: {
    components: React.PropTypes.array,
    component: React.PropTypes.oneOfType([
      React.PropTypes.string,
      React.PropTypes.bool
    ]),
    variation: React.PropTypes.oneOfType([
      React.PropTypes.string,
      React.PropTypes.bool
    ]),
  },

  render() {

    var classes = new bem({
      name: 'component-single',
      prefix: ''
    });

    return (
      <div {...classes()}>
        {this.props.children}
      </div>
    );

  }

});

module.exports = LayoutFull;
