import React from 'react';
import bem from 'react-bem-helper';

const classes = new bem({
  name: 'component-single',
  prefix: ''
});

export default React.createClass({

  displayName: 'LayoutPreview',

  // These are passed from server.js/browser.js
  propTypes: {
    components: React.PropTypes.array.isRequired,
    component: React.PropTypes.object.isRequired,
    children: React.PropTypes.element.isRequired
  },

  componentDidMount() {
    document.documentElement.classList.add('js');
    document.documentElement.classList.add(('ontouchstart' in window) || (navigator.msMaxTouchPoints > 0) || (navigator.maxTouchPoints > 0)
      ? 'touch'
      : 'no-touch');
  },

  render() {
    return (
      <div {...classes()}>
        {this.props.children}
      </div>
    );
  }

});
