'use strict';

var React = require('react');
var Menu = require('styleguide/menu');
var Wrapper = require('styleguide/wrapper');
var Intro = require('styleguide/intro');

var Styleguide = React.createClass({

  displayComponent() {
    var component = this.props.route, rendered;
    if (!component) {
      rendered = <Intro/>;
    } else {
      var path = 'components/' + component;
      var Component = require(path);
      // pass data to component later from .data.json in `path` folder
      // sets size, initial variation and data for render/testing
      rendered = <Component/>;
    }
    return rendered;
  },

  render() {
    return (
      <div className='styleguide'>
        <Menu components={this.props.components} />
        <Wrapper>
          {this.displayComponent()}
        </Wrapper>
      </div>
    );
  }

});

module.exports = Styleguide;
