'use strict';

var React = require('react');
var StyleguideMenu = require('styleguide/menu');
var StyleguideWrapper = require('styleguide/wrapper');

var Styleguide = React.createClass({

  render() {
    return (
      <div className='styleguide'>
        <StyleguideMenu
          components={this.props.components}
          active={this.props.displaying}
        />
        <StyleguideWrapper
          component={this.props.displaying}
        />
      </div>
    );
  }

});

module.exports = Styleguide;
