'use strict';

var React = require('react');
var Main = require('styleguide/main');
var Sidebar = require('styleguide/sidebar');
var Intro = require('styleguide/intro');

var Styleguide = React.createClass({

  getInitialState() {
    return {
      component: this.props.route
    };
  },

  render() {

    return (
      <div className='styleguide'>
        <Sidebar
          components={this.props.components}
          active={this.state.component}
        />
        <Main>
          {this.props.children ? this.props.children : <Intro/>}
        </Main>
      </div>
    );
  }

});

module.exports = Styleguide;
