import React from 'react';
import path from 'path';

export default React.createClass({

  displayName: 'Logo',

  render() {
    const prefix = process.env.STATIC_ASSETS || '/__node__/__static__/client';
    let logoSrc = path.join(prefix, 'images/logos/az_Mobile.svg');
    return (
      <a href='/styleguide' className='styleguide-logo'>
        <img src={logoSrc} alt='Styleguide logo' className='styleguide-logo__image' />
      </a>
    );
  }

});
