const React = require('react');

const Container = require('components/container');
const ContextualNavigation = require('styleguide/contextual-navigation');

const Main = React.createClass({

  propTypes: {
    children: React.PropTypes.element.isRequired,
    active: React.PropTypes.string.isRequired,
    components: React.PropTypes.array.isRequired
  },

  render() {
    return (
      <main role='main' className='styleguide-main'>
        <Container>
          {this.props.children}
        </Container>
        {this.props.active ? <ContextualNavigation components={this.props.components} active={this.props.active} /> : ''}
      </main>
    );
  }

});

module.exports = Main;
