'use strict';

var React = require('react');
var slugToTitle = require('slug-to-title');

var StyleguideMenu = React.createClass({

  menuItems() {
    var components = this.props.components;
    return components.map(function(component) {
      var title = slugToTitle(component.slug);
      var slug = component.slug;
      return <li><a href={'/styleguide/component/' + slug}>{title}</a></li>;
    });
  },

  render() {
    return (
      <header className='styleguide-menu'>
        <nav role="navigation">
          <ul>
            {this.menuItems()}
          </ul>
        </nav>
      </header>
    );
  }

});

module.exports = StyleguideMenu;
