'use strict';

var React = require('react');
var MenuItem = require('styleguide/menu-item');

var Menu = React.createClass({

  menuItems() {
    return this.props.components.map(function(component, index) {
      return <MenuItem key={index} component={component} />;
    });
  },

  render() {
    return (
      <header className='styleguide-menu'>
        <a href='/styleguide'>Home</a>
        <nav role="navigation">
          <ul>
            {this.menuItems()}
          </ul>
        </nav>
      </header>
    );
  }

});

module.exports = Menu;
