'use strict';

const React = require('react');
const bem = require('react-bem-helper');

const MenuItem = require('styleguide/menu-item');

const classes = new bem({
  name: 'menu-list',
  prefix: 'styleguide-'
});

var Menu = React.createClass({

  propTypes: {
    items: React.PropTypes.array,
    switchComponent: React.PropTypes.func
  },

  render() {

    var props = this.props;

    return (
      <ul {...classes()}>
        {props.items.map(function(item, index) {
          return (
            <MenuItem
              {...item}
              key={index}
              switchComponent={props.switchComponent}
            />
          );
        })}
      </ul>
    );

  }

});

module.exports = Menu;
