'use strict';

const React = require('react');
const Markdown = require('react-remarkable');
const slugToTitle = require('slug-to-title');

var ReadMe = React.createClass({

  render() {

    var content;

    if (this.props.readme) {
      content = (
        <div className="readme">
          <Markdown className="readme">{this.props.readme}</Markdown>
        </div>
      );
    } else {
      content = (
        <div className="readme">
          <h1>{slugToTitle(this.props.slug)}</h1>
          <p>You should add a <code>README.md</code> in the root of this component!</p>
        </div>
      );
    }

    return (
      <div>
        {content}
      </div>
    );

  }

});

module.exports = ReadMe;
