import React from 'react';
import bem from 'react-bem-helper';

const classes = new bem({
  name: 'tab',
  prefix: 'styleguide-'
});

export default React.createClass({

  displayName: 'Tab',

  propTypes: {
    children: React.PropTypes.element.isRequired
  },

  render() {
    return (
      <div {...classes()}>
        {this.props.children}
      </div>
    );
  }

});
