'use strict';

const React = require('react');
const bem = require('react-bem-helper');

const classes = new bem({
  name: 'tab',
  prefix: 'styleguide-'
});

const Tab = React.createClass({

  propTypes: {
    children: React.PropTypes.element.isRequired
  },

  render() {
    return (
      <div {...classes()}>
        {this.props.children}
      </div>
    );
  }

});

module.exports = Tab;
