import React from 'react';
import bem from 'react-bem-helper';

const classes = new bem({
  name: 'typography',
  prefix: 'styleguide-'
});

export default React.createClass({

  displayName: 'TypographyItem',

  render() {
    return (
      <section {...classes('item')}>
        <h2 {...classes('title')}>{this.props.title}</h2>
        <p {...classes('description')} dangerouslySetInnerHTML={{ __html: this.props.description }} />
        {this.props.children}
        <pre {...classes('meta')}><code className='language-scss'>{this.props.code}</code></pre>
      </section>
    );
  }

});
