'use strict';

const React = require('react');

var VariationsItem = React.createClass({

  propTypes: {
    title: React.PropTypes.string,
    link: React.PropTypes.string
  },

  render() {
    return (
      <li><a href={this.props.link}>{this.props.title}</a></li>
    );
  }

});

module.exports = VariationsItem;
