'use strict';

const React = require('react');

var VariationsItem = React.createClass({

  propTypes: {
    width: React.PropTypes.string,
    title: React.PropTypes.string,
    data: React.PropTypes.object
  },

  render() {
    return (
      <li><button type='button'>{this.props.title}</button></li>
    );
  }

});

module.exports = VariationsItem;
