import React from 'react';
import bem from 'react-bem-helper';

import VariationsItem from 'styleguide/variations-item';
import Container from 'styleguide/container';

const classes = new bem({
  name: 'variations-list',
  prefix: 'styleguide-'
});

export default React.createClass({

  displayName: 'Variations',

  propTypes: {
    variations: React.PropTypes.array
  },

  render() {
    var variations = this.props.variations;
    var output;

    if (variations) {
      output = (
        <ul {...classes()}>
          {variations.map((variation, index) =>
            <VariationsItem {...variation} key={index} />
          )}
        </ul>
      );
    }

    return output ? (
      <Container>
        <aside>
          {output}
        </aside>
      </Container>
    ) : undefined;
  }

});
