import React from 'react';
import bem from 'react-bem-helper';

import VariationsItem from 'styleguide/variations-item';

const classes = new bem({
  name: 'variations-list',
  prefix: 'styleguide-'
});

export default React.createClass({

  propTypes: {
    variations: React.PropTypes.array
  },

  render() {

    var variations = this.props.variations;
    var output;

    if (variations) {
      output = (
        <ul {...classes()}>
          {variations.map(function(variation, index) {
            return (
              <VariationsItem
                {...variation}
                key={index}
              />
            );
          })}
        </ul>
      );
    }

    return (
      <aside>
        {output}
      </aside>
    );

  }

});
