'use strict';

const React = require('react');
const VariationsItem = require('styleguide/variations-item');

var Variations = React.createClass({

  propTypes: {
    variations: React.PropTypes.array
  },

  render() {

    var variations = this.props.variations;
    var output;

    if (variations) {
      return (
        <ul>
          {variations.map(function(variation, index) {
            return (
              <VariationsItem key={index} {...variation}/>
            );
          })}
        </ul>
      );
    }

    return (
      <aside>
        {output}
      </aside>
    );

  }

});

module.exports = Variations;
