'use strict';

const React = require('react');
const bem = require('react-bem-helper');

const VariationsItem = require('styleguide/variations-item');

const classes = new bem({
  name: 'variations-list',
  prefix: 'styleguide-'
});

var Variations = React.createClass({

  propTypes: {
    variations: React.PropTypes.array
  },

  render() {

    var variations = this.props.variations;
    var output;

    if (variations) {
      output = (
        <ul {...classes()}>
          {variations.map(function(variation, index) {
            return (
              <VariationsItem
                {...variation}
                key={index}
              />
            );
          })}
        </ul>
      );
    }

    return (
      <aside>
        {output}
      </aside>
    );

  }

});

module.exports = Variations;
