'use strict';

const React = require('react');
const ReactToJsx = require('react-to-jsx');
const Highlight = require('react-highlight');
const bem = require('react-bem-helper');

const Tabs = require('styleguide/tabs');
const IFrame = require('styleguide/iframe');

const classes = new bem({
  name: 'viewer',
  prefix: 'styleguide-'
});

const Viewer = React.createClass({

  propTypes: {
    component: React.PropTypes.object.isRequired,
    variation: React.PropTypes.object.isRequired,
    children: React.PropTypes.element.isRequired
  },

  removeTabs(markup) {
    var tab = RegExp("\\t", "g");
    return markup.replace(tab, '  ');
  },

  render() {

    var sections = [
      {
        title: 'Preview',
        content: <IFrame {...this.props} />
      },
      {
        title: 'JSX',
        content: <Highlight className='html'>{this.removeTabs(ReactToJsx(this.props.children))}</Highlight>
      },
      {
        title: 'HTML',
        content: <Highlight className='html'>{React.renderToStaticMarkup(this.props.children)}</Highlight>
      },
    ];

    return (
      <div {...classes()}>
        <Tabs sections={sections} />
      </div>
    );

  }

});

module.exports = Viewer;
