'use strict';

var React = require('react');

var StyleguideWrapper = React.createClass({

  contentToDisplay() {
    var component = this.props.displaying;
    return <h1>{component}</h1>;
  },

  render() {
    return (
      <main role='main' className='styleguide-wrapper'>
        {this.contentToDisplay()}
      </main>
    );
  }

});

module.exports = StyleguideWrapper;
