/* eslint-disable no-console */
import _ from 'lodash';
import deepForEach from 'helpers/deep-foreach';
import QS from 'qs';

export default class NetMetrixTracker {

  static removeEmpty(data) {
    let clone = _.cloneDeep(data);

    return deepForEach(clone, function(e, key, obj) {
      if (e === null || e === '' || typeof(e) === 'undefined' ) {
        delete obj[key];
      }
    });
  }

  static getOrderedPath(data) {
    return [
      data.path.product,
      data.path.sitename,
      data.path.view,
      data.path.path,
      data.path.event,
      data.path.id,
      data.path.index
    ];
  }

  static buildPath(data) {
    let path = '';

    if (data.length && data.length > 0) {
      path = data.join('/');
    }

    return path;
  }

  static buildQS(data) {
    let params = {
      r: (data.referer ? `${encodeURIComponent(data.referer)}&` : ``),
      d: (new Date()).getTime(),
      x: `${data.screenWidth}x${data.screenHeight}`
    };

    return QS.stringify(params);
  }

  static buildTag(data, path) {
    let queryString = NetMetrixTracker.buildQS(data);

    return `https://${data.domain}.wemfbox.ch/cgi-bin/ivw/CP/${path}?${queryString}`;
  }

  static getTag(data) {
    let path,
        cleaned;

    cleaned = NetMetrixTracker.removeEmpty(data);

    cleaned.path = NetMetrixTracker.getOrderedPath(cleaned);

    path = NetMetrixTracker.buildPath(cleaned.path);

    return NetMetrixTracker.buildTag(cleaned, path);
  }

  static requestTag(tag) {
    let image = new Image();

    image.src = tag;
  }

  static log(tag) {
    console.info(`Detected Net Metrix tag not fired. Current host "${window.location.host}" is not configured as live host: `, tag);
  }

  // @TODO: Put into own module, store config globally in .env
  static isLiveHost() {
    let liveHosts = [
      'aargauerzeitung.ch',
      'badenertagblatt.ch',
      'basellandschaftlichezeitung.ch',
      'bzbasel.ch',
      'grenchnertagblatt.ch',
      'limmattalerzeitung.ch',
      'oltnertagblatt.ch',
      'solothurnerzeitung.ch'
    ];


    let isLive = false;
    _.forEach(liveHosts, function(e) {
      if (window && window.location.host.search(e) > -1) {
        isLive = true;
      }
    });

    return isLive;
  }

  static trackEvent(data) {
    let tag = NetMetrixTracker.getTag(data);

    // Only request tag if we are live. Otherwise log to console
    if (NetMetrixTracker.isLiveHost(data)) {
      NetMetrixTracker.requestTag(tag);
    } else {
      NetMetrixTracker.log(tag);
    }

  }

  static trackPageView(data) {
    let tag = NetMetrixTracker.getTag(data);
    NetMetrixTracker.requestTag(tag);

    console.warn('@TODO: trackEvent: ', tag);
  }

}
